function attachPanelChatToggleListeners() {
    const panelChatHeaders = document.querySelectorAll('.panel-chat-header');
    panelChatHeaders.forEach(header => {
        header.addEventListener('click', (event) => {
            // Ensure that clicks on buttons inside the header do not trigger the toggle
            if ((event.target as HTMLElement).tagName.toLowerCase() === 'button') {
                return;
            }

            const panelChatDiv = header.closest('.panel-chat');
            const panelChatContent = panelChatDiv?.querySelector('.panel-chat-content');
            const titleSpan = header.querySelector('.panel-chat-title');

            if (panelChatContent && titleSpan) {
                if (panelChatContent.style.display === 'none') {
                    panelChatContent.style.display = 'block';
                    titleSpan.classList.add('expanded');
                } else {
                    panelChatContent.style.display = 'none';
                    titleSpan.classList.remove('expanded');
                }
            }
        });
    });
}
