        vscode.window.showErrorMessage(`consolidatedGaitData.json is malformed. Reinitializing the file.`);
        fs.writeFileSync(gaitFilePath, JSON.stringify(initialState, null, 2), 'utf-8');
        return initialState;
    }
}
