<!DOCTYPE html>
<html lang="en">
<!-- ... existing HTML ... -->
<script nonce="${nonce}">
    // ... existing JavaScript ...

    window.addEventListener('message', event => {
        const message = event.data;
        if (message.type === 'update') {
            // ... existing code ...

