import posthog from "posthog-js";

import simpleGit, { SimpleGit } from 'simple-git';
import * as vscode from 'vscode';
import * as crypto from 'crypto';

export async function identifyUser(context: vscode.ExtensionContext): Promise<void> {
    const git: SimpleGit = simpleGit();

    try {
        const userEmail = await git.raw(['config', '--get', 'user.email']);
        const trimmedEmail = userEmail.trim();
        const firstCommitHash = await git.raw(['rev-list', '--parents', 'HEAD']).then(output => {
            const lines = output.trim().split('\n');
            return lines[lines.length - 1].split(' ')[0];
        });
        if (trimmedEmail) {
            const hashedEmail = crypto.createHash('sha256').update(trimmedEmail).digest('hex');
            context.globalState.update('posthog_id', hashedEmail);
            context.globalState.update('email', trimmedEmail);

            posthog.identify(hashedEmail, { email: trimmedEmail });
        } else {
            console.log('No Git user email found');
        }
    } catch (error) {
        console.error('Error identifying user:', error);
    }
}

