            }

            // If we have at least two meaningful lines, add the range
            if (meaningfulLines >= 2) {
                for (let j = start; j <= end; j++) {
                    multiLineRanges.push(new vscode.Range(j, 0, j, documentLines[j].length));
                }
            }

            start = -1;
            end = -1;
        }
    }

    return multiLineRanges;
}



