                                            const fullPath = item.value.human_readable;
                                            let relativePath = fullPath;

                                            if (workspaceFolderPath && fullPath.startsWith(workspaceFolderPath)) {
                                                relativePath = fullPath.slice(workspaceFolderPath.length + 1);
