        fs.appendFileSync(gitAttributesPath, `\n${GAIT_FOLDER_NAME}/** -diff -linguist-generated=true\n`);
        vscode.window.showInformationMessage('.gitattributes updated successfully');
    }

// Add .gait folder to git
