        }

        .inline-chat-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
        }

        .inline-chat-header:hover {
            background-color: var(--vscode-editor-selectionBackground);
        }

        .inline-chat-details {
            display: none;
            margin-top: 10px;
        }

        .inline-chat-prompt {
            margin-bottom: 5px;
        }

        .file-diff {
            margin-left: 20px;
            background-color: var(--vscode-textCodeBlock-background);
            padding: 8px;
            border-radius: 3px;
            overflow-x: auto;
        }

        .commit-delineator {
            margin: 20px 0;
        }

        .commit-delineator hr {
            border: none;
            border-top: 1px solid var(--vscode-editorWidget-border);
        }

        .commit-delineator h3 {
            text-align: center;
            color: var(--vscode-descriptionForeground);
            font-size: 1.2em;
            margin: 10px 0;
        }

        /* Dark mode syntax highlighting */
        .vscode-dark pre[class*="language-"],
        .vscode-dark code[class*="language-"] {
            color: #f8f8f2;
            background: none;
            text-shadow: 0 1px rgba(0, 0, 0, 0.3);
            font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
            font-size: 1em;
            text-align: left;
            white-space: pre;
            word-spacing: normal;
            word-break: normal;
            word-wrap: normal;
            line-height: 1.5;
            tab-size: 4;
            hyphens: none;
        }

        .vscode-dark .token.comment,
        .vscode-dark .token.prolog,
        .vscode-dark .token.doctype,
        .vscode-dark .token.cdata {
